operation.add:
$status_correiospro5 = $this->config->get('correiospro5_status');
if($status_correiospro5==1){
	$correiospro5 = array();
	$correiospro5[] = array(
		'name'	   => 'Servi&ccedil;os Cadastrados',
		'href'     => $this->url->link('extension/shipping/correiospro5', 'tab=servicos&token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	$correiospro5[] = array(
		'name'	   => 'Tabelas Offline',
		'href'     => $this->url->link('extension/shipping/correiospro5/tabelas', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	$correiospro5[] = array(
		'name'	   => 'Faixas de CEPs',
		'href'     => $this->url->link('extension/shipping/correiospro5/faixas', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	$correiospro5[] = array(
		'name'	   => 'Imprimir Etiquetas',
		'href'     => $this->url->link('extension/shipping/correiospro5/etiquetas', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	$correiospro5[] = array(
		'name'	   => 'Configura&ccedil;&otilde;es',
		'href'     => $this->url->link('extension/shipping/correiospro5', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	$correiospro5[] = array(
		'name'	   => 'Logs',
		'href'     => $this->url->link('extension/shipping/correiospro5/logs', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
	if ($correiospro5) {
		$data['menus'][] = array(
			'id'       => 'menu-correiospro5',
			'icon'	   => 'fa-truck', 
			'name'	   => 'Fretes Correios',
			'href'     => '',
			'children' => $correiospro5
		);	
	}				
}

$status_correiospro5 = $this->config->get('correiospro5_status');
if($status_correiospro5==1){
	$data['correiospro5_config'] = $this->url->link('shipping/correiospro5', 'token=' . $this->session->data['token'], true);
	$data['correiospro5_servicos'] = $this->url->link('shipping/correiospro5', 'tab=servicos&token=' . $this->session->data['token'], true);
	$data['correiospro5_tabelas'] = $this->url->link('shipping/correiospro5/tabelas', 'token=' . $this->session->data['token'], true);
	$data['correiospro5_faixas'] = $this->url->link('shipping/correiospro5/faixas', 'token=' . $this->session->data['token'], true);
	$data['correiospro5_etiquetas'] = $this->url->link('shipping/correiospro5/etiquetas', 'token=' . $this->session->data['token'], true);
	$data['correiospro5_logs'] = $this->url->link('shipping/correiospro5/logs', 'token=' . $this->session->data['token'], true);
}
